import React, { useEffect, useRef, useState } from 'react';
import axios from "axios";
import { LazyLoadImage } from 'react-lazy-load-image-component';

function OurPresenceComponent() {
    let publicPath = process.env.PUBLIC_URL;
    const [ourpresenceLoading, setOurPresenceLoading] = useState(false);
    const [ourpresence, setOurPresence] = useState([]);

    async function fetchOurPresence() {
        setOurPresenceLoading(true);
        try {
            const res = await axios.get(publicPath + "/assets/data/OurPresence.json");
            if (res.data) {
                setOurPresenceLoading(false);
                setOurPresence(res.data);
            }
        } catch (error) {
            console.error("error ", error);
            setOurPresenceLoading(false);
        }
    }

    useEffect(() => {
        window.scrollTo(0, 0);
        fetchOurPresence();
    }, []);

    return (
        <>
            <div className="ourPresenceBox">
                <div className="container">
                    <div className="row text-center">
                        <div className="col-md-12">
                            <div className="ourPresenceHeadingSection">
                                <small>lorem ipsum quia dolor sit amet</small>
                                <h2>Our Presence</h2>
                            </div>
                        </div>
                    </div>
                    <div className="ourPresenceContent" ref={(node) => {
                        if (node) {
                            node.style.setProperty("background", `url(${publicPath}/assets/images/mapp.png)`, "important");
                            node.style.setProperty("background-repeat", `no-repeat`, "important");
                            node.style.setProperty("background-size", `100%`, "important");
                            node.style.setProperty("background-position", `left 86%`, "important");
                        }
                    }}>
                        <div className="row justify-content-center">
                            {ourpresence && ourpresence.map((row, index) => {
                                return (
                                    <div className="col-md-4" key={index}>
                                        <div className="indiaCountry">
                                            <h4>{row.country_name}</h4>
                                            <div className="countryContactDetails">
                                                <div className="d-flex justify-content-between">
                                                    <div className="col-1">
                                                        <LazyLoadImage alt="" src={publicPath + row.address_logo} className="img-fluid"/>
                                                    </div>
                                                    <div className="col-10">
                                                        <p className="indiaCountryPara">{row.country_address}</p>
                                                    </div>
                                                </div>
                                                <div className="d-flex justify-content-between">
                                                    <div className="col-1">
                                                        <LazyLoadImage alt="" src={publicPath + row.contact_logo} className="img-fluid"/>
                                                    </div>
                                                    <div className="col-10">
                                                        <p className="indiaCountryPara">{row.contact_numbder}</p>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                )
                            })}
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}
export default OurPresenceComponent;
