import { useEffect, useState } from 'react';
import axios from 'axios';
import { API_ENDPOINT } from "../../common/Constants";



function SocialSharingComponent(){
    let publicPath = process.env.PUBLIC_URL;

    const [footersocialLoading, setFooterSocialLoading] = useState(false);
    const [footerSocialLinks, setFooterSocialLinks] = useState(null);


    const [countriesLoading, setCountriesLoading] = useState(false);
    const [countries, setCountries] = useState([]);

    const [projectsLoading, setProjectsLoading] = useState(false);
    const [projects, setProjects] = useState([]);


    const [loading, setLoading] = useState(false);
    const [formData, setFormData] = useState({ first_name: '', last_name: '', email: '', country: '', dial_code: '', mobile: '', message: '', appointment_date: '', appointment_time: '', terms: false });

    useEffect(() => {
        fetchFooterSocial();
        fetchProjects();
        fetchCountries();
        setLoading(false);
    }, []);


    async function fetchFooterSocial() {
        setFooterSocialLoading(true);
        try {
            const res = await axios.get(publicPath + "/assets/data/social.json");
            if (res.data) {
                setFooterSocialLoading(false);
                setFooterSocialLinks(res.data.social_links);
            }
        } catch (error) {
            console.error("error ", error);
            setFooterSocialLoading(false);
        }
    }
    async function fetchProjects() {
        setCountriesLoading(true);
        try {
            const res = await axios.get(API_ENDPOINT + "projects/list");
            if (res.data.data) {
                setCountriesLoading(false);
                setProjects(res.data.data.projects);
            }
        } catch (error) {
            console.error("error ", error);
            setCountriesLoading(false);
        }
    }

    async function fetchCountries() {
        setCountriesLoading(true);
        try {
            const res = await axios.get(publicPath + "/assets/data/countries.json");
            if (res.data) {
                //console.log("res.data ",res.data.data);
                setCountriesLoading(false);
                setCountries(res.data.data);
            }
        } catch (error) {
            console.error("error ", error);
            setCountriesLoading(false);
        }
    }
    return(
        <>
        <div className="row mb-2 mt-4">
                <div className="col text-center">
                    <h5 className="contactWithUsHeading"><strong>Contact Us</strong></h5>
                </div>
            </div>
            <div className="row text-center justify-content-center">
                {footerSocialLinks ?
                    <>
                        <div className="col-auto ">
                            <div className="formPageLogo">
                                <a target="_blank" href={footerSocialLinks ? footerSocialLinks.whatsapp : ''} rel="noreferrer" className="watsup position-relative">
