import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { useNavigate } from 'react-router-dom';
import axios from "axios";
import { API_ENDPOINT } from '../common/Constants';


function TwoDSystemPage() {
  let { id } = useParams();
  let navigate = useNavigate();
  const [projectLoading, setProjectLoading] = useState(false);
  const [project, setProject] = useState(null);

  async function fetchProject() {
    setProjectLoading(true);
    try {
      const res = await axios.get(API_ENDPOINT + "projects/show/" + id);
      if (res.data.data) {
        setProjectLoading(false);
        setProject(res.data.data);
      }
    } catch (error) {
      console.error("error ", error);
      setProjectLoading(false);
    }
  }


  useEffect(() => {
    window.scrollTo(0, 0);
    fetchProject();
  }, []);

  return (
    <>
      {projectLoading ?
        <>
          <div className="text-center">Loading...</div>
        </>
        :
        <>
          {project ?
            <div>
              {project.is_2d_enabled ?
                <>
                  <div className="col-auto twoDbackBtn">
                    <button className="cusocialBackBtn" onClick={() => navigate(-1)}>
                      <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-arrow-left" viewBox="0 0 16 16">
                        <path fillRule="evenodd" d="M15 8a.5.5 0 0 0-.5-.5H2.707l3.147-3.146a.5.5 0 1 0-.708-.708l-4 4a.5.5 0 0 0 0 .708l4 4a.5.5 0 0 0 .708-.708L2.707 8.5H14.5A.5.5 0 0 0 15 8z" />
                      </svg> Back
                    </button>
                  </div>
                  <iframe title="2DSales" src={`${project.portal_2d_url}`} width="100%" height="100%" frameBorder="0" style={{ border: 0, height: 'calc(100vh - 43px)' }} loading="lazy" allowfullscreen="allow"></iframe></>
                :
                <><div className="text-center">2d system is not available.</div></>
              }
            </div>
            :
            <><div className="text-center">No data available.</div></>
          }
        </>
      }
    </>
  );
}

export default TwoDSystemPage;
