import { useEffect, useRef, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import "react-multi-carousel/lib/styles.css";
import Moment from 'react-moment';
import axios from "axios";
import { LazyLoadImage } from 'react-lazy-load-image-component';

import { API_ENDPOINT } from "../common/Constants";

function CuSocialDetailPage(props) {
    let publicPath = process.env.PUBLIC_URL;
    let navigate = useNavigate();
    let { id } = useParams();
    const [cuSocialLoading, setCuSocialLoading] = useState(false);
    const [cuSocial, setCuSocial] = useState(null);

    async function fetchCuSocial() {
        setCuSocialLoading(true);
        console.log("id ", id);
        try {
            const res = await axios.get(API_ENDPOINT + "cusocial/show/" + id);
            setCuSocialLoading(false);
            console.log("res.data.data", res.data.data);
            if (res.data) {
                setCuSocial(res.data.data);
            }
        } catch (error) {
            console.error("error ", error);
            setCuSocialLoading(false);
        }
    }

    useEffect(() => {
        window.scrollTo(0, 0);
        fetchCuSocial();
    }, []);



    return (
        <>
            <div className="CuSocialDetailPage mt-5 pt-5">
                <div className="container-fluid">
                    <div className="row">
                        <div className="col-auto">
                            <button className="btn" onClick={() => navigate(-1)}>
                                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-arrow-left" viewBox="0 0 16 16">
                                    <path fillRule="evenodd" d="M15 8a.5.5 0 0 0-.5-.5H2.707l3.147-3.146a.5.5 0 1 0-.708-.708l-4 4a.5.5 0 0 0 0 .708l4 4a.5.5 0 0 0 .708-.708L2.707 8.5H14.5A.5.5 0 0 0 15 8z" />
                                </svg> Back
                            </button>
                        </div>
                        <div className="col"><h3>CuSocial</h3></div>
                    </div>
                </div>


                <div className="container mt-2 mb-5">

                    {cuSocialLoading ?
                        <>
                            <div className="row">
                                <div className="col text-center">Loading...</div>
                            </div>
                        </>
                        :
                        <>
                            {cuSocial ?
                                <>
                                    <div className="row">

                                        <div className="col">
                                            <div><h1>{cuSocial.title}</h1></div>
                                            <div>{cuSocial.category.toUpperCase()} | <Moment fromNow>{cuSocial.created_at}</Moment></div>
                                        </div>
                                    </div>
                                    <div className="row">
                                        <div className="col">
                                            {cuSocial.featured_image ?
                                                <LazyLoadImage alt="" src={cuSocial.media_s3_base_path + cuSocial.featured_image} className="img-fluid" />
                                                :
                                                <LazyLoadImage alt="" src={publicPath + '/assets/images/newsfeed.jpeg'} className="img-fluid" />
                                            }
                                        </div>
                                        <div className="col">
                                            <div dangerouslySetInnerHTML={{ __html: `${cuSocial.long_description}` }} />
                                        </div>
                                    </div>
                                </>
                                :
                                <>
                                    <div className="row">
                                        <div className="col text-center">404.</div>
                                    </div>
                                </>
                            }

                        </>

                    }
                </div>

            </div>
        </>
    );
}
export default CuSocialDetailPage;
