import { useEffect } from "react";
import "animate.css/animate.min.css";
// import { AnimationOnScroll } from 'react-animation-on-scroll';
import AnimationOnScroll from '../components/AnimationOnScroll';
import { Link } from "react-router-dom";
// import { fadeIn } from 'react-animations';
// import Radium, {StyleRoot} from 'radium';

import { LazyLoadImage } from 'react-lazy-load-image-component';
import 'react-lazy-load-image-component/src/effects/blur.css';
import CharactersLimitComponent from "./CharactersLimitComponent";

let publicPath = process.env.PUBLIC_URL;

function ProjectList(props) {
    const { row } = props;
    // const styles = {
    //     bounce: {
    //       animation: 'x 1s',
    //       animationName: Radium.keyframes(fadeIn, 'fadeIn')
    //     }
    //   }

    useEffect(() => {
        //
    }, [props]);


    return (
        <>
         <AnimationOnScroll animateIn="animate__pulse" animateOnce={true}>
            <div className="listItem" >
                <div className="container" style={{
                    // 'boxShadow': '0px 1px 4px 2px rgba(0,0,0,0.22)',
                    // 'WebkitBoxShadow': '0px 1px 4px 2px rgba(0,0,0,0.22)',
                    // 'MozBoxShadow': '0px 1px 4px 2px rgba(0,0,0,0.22)'
                }}>
                    <div className="ProjectDetailContent">
                        <div className="row">
                            <div className="col-md-5">
                                <div className="row">
                                    <div className="col-md-8 pe-0">
                                        <LazyLoadImage
                                            effect="blur"
                                            src={publicPath + row.banner_images[0]}
                                            placeholderSrc={publicPath + "/assets/images/thumbnail.png"}
                                            className="img-fluid"
                                            visibleByDefault={false}
                                            wrapperClassName="mainBox"
                                            alt="" />

                                        {/* <img src={publicPath + row.banner_images[0]} alt="" className="img-fluid" /> */}
                                    </div>

                                    <div className="col-md-4 ps-2">
                                        <LazyLoadImage
                                            effect="blur"
                                            src={publicPath + row.banner_images[1]}
                                            placeholderSrc={publicPath + "/assets/images/thumbnail.png"}
                                            className="img-fluid smallImg"
                                            visibleByDefault={false}
                                            width="135"
                                            alt="" />
                                        <LazyLoadImage
                                            effect="blur"
                                            src={publicPath + row.banner_images[2]}
                                            placeholderSrc={publicPath + "/assets/images/thumbnail.png"}
                                            className="img-fluid smallImg mt-2"
                                            width="135"
                                            visibleByDefault={false}
                                            alt="" />
                                        {/* 
                                        <img src={publicPath + row.banner_images[1]} alt="" className="img-fluid smallImg" width="135" />
                                        <img src={publicPath + row.banner_images[2]} alt="" className="img-fluid smallImg mt-2" width="135" /> 
                                        */}
                                    </div>
                                    {/* <span className="noofimg">{row.banner_images.length}</span> */}
                                </div>
                            </div>
                            <div className="col-md-7">
                                <div className="projectdetailss">
                                    <div className="row">
                                        <div className="col-md-8">
                                            <h5 className="projectDetailsHeading"><Link to={`/projects/${row.slug}`}>{row.title}</Link></h5>
                                            <small>{row.city}, {row.country}</small>
                                        </div>
                                        <div className="col-md-4">
                                            <div className="pricing">
                                                <h5>{row.currency}{row.price_min} - {row.currency}{row.price_max}</h5>
                                                <small>Book</small>
                                            </div>
                                        </div>
                                    </div>

                                    <div className="mt-2"> 
                                    {/* {row.description} */}
                                    <CharactersLimitComponent isShowbutton={false} content = {row.description} limit = {350} />
                                    </div>

                                    <p className="mb-0"><span>Status : </span> Lorem ipsum dolor sit amet, consectetur adipiscing</p>

                                    <div className="shareButtonSection">
                                        <div className="row">
                                            <div className="col-md-9">
                                                <div className="row">
                                                    <div className="col">
                                                        <button className="shareBtn">
                                                            <img src={publicPath + "/assets/images/2D.png"} width="30" alt="" className="img-fluid" />
                                                            Sales System
                                                        </button>
                                                    </div>

                                                    <div className="col">
                                                        <button className="shareBtn">
                                                            <img src={publicPath + "/assets/images/3D.png"} width="30" alt="" className="img-fluid" />
                                                            Sales System
                                                        </button>
                                                    </div>
                                                    <div className="col">
                                                        <button className="shareBtn">
                                                            <img src={publicPath + "/assets/images/inventory.png"} width="30" alt="" className="img-fluid" />
                                                            Inventory
                                                        </button>
                                                    </div>
                                                </div>
                                            </div>

                                            <div className="col-md-3">

                                                <button type="button" className="btn me-1 ratingBtn">
                                                    <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-heart" viewBox="0 0 16 16">
                                                        <path d="m8 2.748-.717-.737C5.6.281 2.514.878 1.4 3.053c-.523 1.023-.641 2.5.314 4.385.92 1.815 2.834 3.989 6.286 6.357 3.452-2.368 5.365-4.542 6.286-6.357.955-1.886.838-3.362.314-4.385C13.486.878 10.4.28 8.717 2.01L8 2.748zM8 15C-7.333 4.868 3.279-3.04 7.824 1.143c.06.055.119.112.176.171a3.12 3.12 0 0 1 .176-.17C12.72-3.042 23.333 4.867 8 15z" />
                                                    </svg>
                                                </button>

                                                <button type="button" className="btn contactBtn">Contact</button>

                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            </AnimationOnScroll>
        </>
    );
}
export default ProjectList;
