                                                    effect="blur"
                                                    src={row.banners[0].media_s3_base_path + row.banners[0].local_path}
                                                    placeholderSrc={publicPath + "/assets/images/thumbnail.png"}
                                                    className="img-fluid"
                                                    visibleByDefault={true}
                                                    alt={row.title} />
