import { useState, useEffect } from "react";
import axios from "axios";
import { useAuthHeader } from 'react-auth-kit';
import DateRangePicker from '@wojtekmaj/react-daterange-picker';
import 'react-calendar/dist/Calendar.css';
import { API_ENDPOINT } from "../../common/Constants";
import { Button } from "bootstrap";
import Moment from 'react-moment';
import moment from 'moment';

function AppointmentDataList() {
    const authHeader = useAuthHeader();
    const startOfMonth = moment().startOf('month').format('YYYY-MM-DD');
    const endOfMonth   = moment().endOf('month').format('YYYY-MM-DD');
    const [dateRange, onChangeDateRange] = useState([new Date(startOfMonth), new Date(endOfMonth)]);
    const [dashboardSalesStatsLoader, setDashboardSalesStatsLoader] = useState(false);
    const [dashboardSalesStats, setDashboardSalesStats] = useState([]);
    const [formData, setFormData] = useState({ dfilter: null });
    useEffect(() => {
        fetchDashboardAppointmentStatsData();
        //console.log("dateRange",dateRange);
    }, []);

    const handleChangeFilter = (e) => {
        e.persist();
        let selectedFilterValue = e.target.value;
        setFormData(formData => ({ ...formData, test: selectedFilterValue }));
        fetchDashboardAppointmentStatsData();
    }

    /* SALES LIST */
    async function fetchDashboardAppointmentStatsData() {
        setDashboardSalesStatsLoader(true);
        console.log("dateRange ",dateRange);
        let postData = {
            
            start_date: dateRange[0],
            end_date: dateRange[1]
        };
        try {
            const res = await axios.post(API_ENDPOINT + "orgzit/appointments", postData, { headers: { Authorization: authHeader() } });
            setDashboardSalesStatsLoader(false);
            setDashboardSalesStats(res.data.data);

        } catch (error) {
            console.error("error ", error);
            setDashboardSalesStatsLoader(false);
        }
    }

    return (
        <>
            <div className="reservationAgentContent" >
                <div className="d-flex justify-content-between my-2 align-items-center" >
                    <div>
                        <h6>Appointments</h6>
                    </div>
                    <div>
                        <select name="dfilter" id="dfilter" onChange={handleChangeFilter}>
                            <option selected value="All" >All</option>
                            <option value="thisweek" >Current Week</option>
                            <option value="nextweek" >Next Week</option>
                            <option value="lastweek" >Last Week</option>
                            <option value="thismonth" >This Month</option>
                            <option value="nextmonth" >Next Month</option>
                            <option value="lastmonth" >Last Month</option>
                        </select>
                    </div>
                </div>
                
                {dashboardSalesStatsLoader ? <tr><td colSpan="6" align="center">Loading...</td></tr> : null}
                    {dashboardSalesStats.length > 0 && dashboardSalesStats.map((row, index) =>
                        <div className="teamMeetingContent" key={index}>        
                            <span className="d-flex justify-content-between my-2 align-items-left">
                                {row.source}
                            </span>
                            {/*<span>
                                <small>{row.stage}</small>
                            </span>*/}
                            <span>
                                <h6>{row.createdDate}</h6>
                                <small>{row.createdTime}</small>
                            </span>
                        </div>
                    )}
                
            </div>        
        </>
    );
}

export default AppointmentDataList;
