import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import _ from "lodash";
let publicPath = process.env.PUBLIC_URL;
const apidatass = [
  {
      "id":"1",
      "img":publicPath + "/assets/images/home/01.jpg",
      "title":"Yamanevler",
      "category":"yaman",
      "slug":"yamanevler",
      "subtitle":"The ideal family-friendly residence"
  },
  {
      "id":"2",
      "img":publicPath + "/assets/images/home/02.jpg",
      "title":"Skyland",
      "category":"Skyland",
      "slug":"skyland",
      "subtitle":"Lorem ipsum dolor sit amet"
  },
  {
      "id":"3",
      "img":publicPath + "/assets/images/home/04.jpg",
      "title":"Alya 4 Mevsim",
      "slug":"alya4mevsim",
      "category":"alya",
      "subtitle":"Affordable luxury at its finest"
  },
  {
      "id":"4",
      "img":publicPath + "/assets/images/home/01.jpg",
      "title":"Acar Blu",
      "slug":"acar-blu",
      "category":"blu",
      "subtitle":"A life of prestige with unlimited privileges "
  },
  {
      "id":"5",
      "img":publicPath + "/assets/images/home/02.jpg",
      "title":"Nisantasi",
      "slug":"nisantasi",
      "category":"Nisantasi",
      "subtitle":"The icon of luxurious living"
  }
];
export default function ProjectsMapComponent(props) {
  const [filterData, setFilterData] = useState(null);
  const svgLoad = () => {
    const objTag = document.querySelector('object');
    //console.log("objTag", objTag);
    // wait for SVG to load
    //setTimeout(()=>{
    objTag.addEventListener('load', () => {

      // reference to SVG document
      const svg = objTag.getSVGDocument();
      //console.log("svg", svg);

      // make changes
      Array.from(svg.getElementsByClassName('location'))
        .forEach(p => {
          p.setAttribute('cursor', 'pointer')
          console.log("path",p,p.getAttribute('id'));
          // let svg = p.querySelector('id');
          p.addEventListener('mouseover', handlePlaceClick)
        }
        )

    });
 // },2000)
  }
  const handlePlaceClick = (ev) => {
    let t = ev.target
    console.log("handlePlaceClick", t.getAttribute('id'));
    let currentId = t.getAttribute('id');
    //let mapdatafilter = mapData();

    //console.log("mapdatafilter", mapdatafilter);
    let filterData = _.filter(apidatass, {
      category: currentId
    });
    setFilterData(filterData[0]);
    console.log("filterData", apidatass);
  }
  useEffect(() => {
      svgLoad();
  }, [])
  return (
    <div className='mapHome'>
      <object
        data={publicPath + "/assets/images/map/Istanbulmap.svg"}
        aria-label="block view"/>
      {filterData ? 
        <div className='col-md-2 projectDetailBox'>
          <img alt="" className='img-fluid' src={filterData.img} />
          <h2><Link to={`/projects/${filterData.slug}`}>{filterData.title}</Link></h2>
          <p>{filterData.subtitle}</p>
        </div>
      :
      ''
      }
    </div>
  );
}

