import { Link } from 'react-router-dom';

import { useEffect, useState } from 'react';
import axios from 'axios';
import Spinner from 'react-bootstrap/Spinner';
import { API_ENDPOINT } from "../../common/Constants";

function EnquireAboutThisPropertyComponent() {

    let publicPath = process.env.PUBLIC_URL;

    const [countriesLoading, setCountriesLoading] = useState(false);
    const [countries, setCountries] = useState([]);

    const [loading, setLoading] = useState(false);
    const [formData, setFormData] = useState({ first_name: '', last_name: '', email: '', country: '', dial_code: '', mobile: '', message: '' });

    useEffect(() => {
        setLoading(false);
        fetchCountries();
    }, []);

    async function fetchCountries() {
        setCountriesLoading(true);
        try {
            const res = await axios.get(publicPath + "/assets/data/countries.json");
            if (res.data) {
                //console.log("res.data ",res.data.data);
                setCountriesLoading(false);
                setCountries(res.data.data);
            }
        } catch (error) {
            console.error("error ", error);
            setCountriesLoading(false);
        }
    }

    const handleChangeCountry = (e) => {
        e.persist();
        let selectedCountryName = e.target.value;
        setFormData(formData => ({ ...formData, country: selectedCountryName }));
        let countriesList = countries;
        let newDialCode = Object.keys(countriesList).filter((x) => {
            return countriesList[x].country_name === selectedCountryName;
        }).map((x) => {
            return countriesList[x].dial_code;
        });
        let dial_code = newDialCode.length ? newDialCode[0] : '';
        setFormData(formData => ({ ...formData, dial_code: dial_code }));
    }

    const resetFrom = () => {
        setFormData({ first_name: '', last_name: '', email: '', country: '', dial_code: '', mobile: '', message: '' });
        document.getElementById("form2").reset();
    }

    const onSubmit = (e) => {
        e.preventDefault();
        console.log("formData ", formData);
        setLoading(true);

        axios.post(API_ENDPOINT + 'orgzit/requestEnrollment', formData)
            .then((res) => {
                console.log('res ### ', res.status, res.data)
                if (res.status === 200) {
                    setLoading(false);
                    resetFrom();
                    setTimeout(() => {
                        alert(res.data.message);
                    }, 1000);
                }
            }).catch((error) => {
                setLoading(false);
                console.log("errors ### ", error);
                if (error) {
                    if (error.response.status === 422) {
                        let errors = error.response.data.errors;
                        let msg = error.response.data.message + "\n";
                        Object.keys(errors).map((error, index) => (
                            msg += errors[error][0] + "\n"
                        ));
                        alert(msg);
                    }
                }
            })
    }

    return (
        <>
            <div className="enquireForm sticky-top">
                <h3>Enquire about this Property</h3>
                <form id="form2" onSubmit={onSubmit}>

                    <div className="mb-3">
                        <label className="form-label">First Name</label>
                        <input type="text" className="form-control" placeholder="First Name" name="first_name" defaultValue={formData.first_name} onChange={(e) => setFormData({ ...formData, first_name: e.target.value })} />
                    </div>
                    <div className="mb-3">
                        <label className="form-label">Last Name</label>
                        <input type="text" className="form-control" placeholder="Last Name" name="last_name" defaultValue={formData.last_name} onChange={(e) => setFormData({ ...formData, last_name: e.target.value })} />
                    </div>
                    <div className="mb-3">
                        <label className="form-label">Email Address</label>
                        <input type="email" className="form-control" placeholder="Email Address" name="email" defaultValue={formData.email} onChange={(e) => setFormData({ ...formData, email: e.target.value })} />
                    </div>

                    <div className="mb-3">
                        <label className="form-label">Country</label>
                        {countries.length > 0 ?
                            <>
                                <select className="form-select" placeholder="Country" name="country" onChange={handleChangeCountry} defaultValue={formData.country_name}>
                                    <option value="">Select Country</option>
                                    {countries.length > 0 && countries.map((row, index) => <option value={row.country_name} key={index} >{row.country_name}</option>)}
                                </select>
                            </>
                            : null}
                    </div>

                    <div className="mb-3">
                        <label className="form-label">Mobile</label>

                        <div className="input-group mb-3">
                            <span className="input-group-text" id="basic-addon1">{formData.dial_code ? formData.dial_code : '+91'}</span>
                            <input type="text" className="form-control" placeholder="Mobile" name="mobile" defaultValue={formData.mobile} onChange={(e) => setFormData({ ...formData, mobile: e.target.value })} />
                        </div>


                    </div>

                    <div className="mb-4">
                        <label className="form-label">Message</label>
                        <textarea className="form-control" placeholder="Write Your Message" name="message" defaultValue={formData.message} onChange={(e) => setFormData({ ...formData, message: e.target.value })}></textarea>
                    </div>

                    {loading ?
                        <button className="btntheme" type="button" disabled >
                            <Spinner as="span" animation="border" size="sm" role="status" aria-hidden="true" />  Submitting...
                        </button>
                        :
                        <button className="btntheme" type="submit">Book Appointment</button>
                    }


                </form>
                <div className="termsAndConditionSection ">
                                                <small>By clicking the submit button below, I hereby agree to and accept the following terms and conditions policy.</small>
                                                <small><Link to={""}>Terms & Condition</Link></small>
                                            </div>
            </div>
        </>
    );

}

export default EnquireAboutThisPropertyComponent;
