// Import Swiper React components
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/swiper-bundle.min.css'
// Import Swiper styles
// import 'swiper/css';
import React, { useEffect, useRef, useState } from 'react';



function SimpleSwiper() {
  let publicPath = process.env.PUBLIC_URL;



  const onPrevClick = () => {
    carouselRef.current.prev();
  };
  const onNextClick = () => {
    carouselRef.current.next();
  };
  const carouselRef = useRef(null);


  return (
    <>
      <Swiper
        spaceBetween={20}
        slidesPerView={2}
        onSlideChange={() => console.log('slide change')}
        onSwiper={(swiper) => console.log(swiper)}
      >
        <SwiperSlide>
          <img src="./assets/images/home/01.jpg" className="img-fluid" />
        </SwiperSlide>
        <SwiperSlide>
          <img src="./assets/images/home/02.jpg" className="img-fluid" />
        </SwiperSlide>
        <SwiperSlide>
          <img src="./assets/images/home/03.jpg" className="img-fluid" />
        </SwiperSlide>
        <SwiperSlide>
          <img src="./assets/images/home/04.jpg" className="img-fluid" />
        </SwiperSlide>
      </Swiper>
      <div className="carouselNav">
        <button className="carousel-control-prev" type="button" data-bs-target="#carouselExampleControls" data-bs-slide="prev">
          <span className="carousel-control-prev-icon" aria-hidden="true">
            <img alt="" src={publicPath + "/assets/images/left.svg"} />
          </span>
          <span className="visually-hidden">Previous</span>
        </button>
        <button className="carousel-control-next" type="button" data-bs-target="#carouselExampleControls" data-bs-slide="next">
          <span className="carousel-control-next-icon" aria-hidden="true">
            <img alt="" src={publicPath + "/assets/images/right.svg"} />
          </span>
          <span className="visually-hidden">Next</span>
        </button>
      </div>
    </>
  );
};

export default SimpleSwiper;
