import React, { useEffect, useRef, useState } from 'react';
import "react-multi-carousel/lib/styles.css";
import { useParams, Link } from 'react-router-dom';
import { LazyLoadImage } from 'react-lazy-load-image-component';
import { Player, ControlBar, BigPlayButton } from 'video-react';


import Moment from 'react-moment';
import axios from "axios";
import "video-react/dist/video-react.css";
import { API_ENDPOINT } from "../common/Constants";
import CharactersLimitComponent from "../components/CharactersLimitComponent";
import VideoPopUp from "../components/update/VideoPopUp";
import SocialShareButtons from "../components/update/SocialShareButtons";

function CuSocialPage(props) {
    let publicPath = process.env.PUBLIC_URL;
    let { category } = useParams();

    const [isVideoModalShow, setIsVideoModalShow] = useState(false);
    const [activeVideoData, setActiveVideoData] = useState(null);


    const [cuSocialsLoading, setCuSocialsLoading] = useState(false);
    const [cuSocials, setCuSocials] = useState([]);

    const showVideoModal = (r) => {
        console.log("showVideoModal clicked");
        setActiveVideoData(r);
        setIsVideoModalShow(true);
    }

    const closeVideoModal = () => {
        console.log("closeVideoModal trigger");
        setIsVideoModalShow(false);
        setActiveVideoData(null);
    }

    async function fetchUpdates(category) {
        setCuSocialsLoading(true);
        console.log('category ', category);
        try {
            const res = await axios.get(API_ENDPOINT + "cusocial/latestByCategory/" + category);
            if (res.data.data) {
                setCuSocialsLoading(false);
                setCuSocials(res.data.data);
            }
        } catch (error) {
            console.error("error ", error);
            setCuSocialsLoading(false);
        }
    }

    useEffect(() => {
        window.scrollTo(0, 0);
        fetchUpdates(category);
    }, [category]);


    return (
        <div className='mt-70'>
            <div className='UpdatesSection'>
                <div className="bannerAboutUs">
                    {/* <img src={publicPath + "/assets/images/aboutus.jpg"} className="img-fluid w-100" /> */}
                    <h1>CuSocial</h1>
                </div>
                <div className='container'>
                    <div className="cuSocialMainTabs">
                        <ul>
                            <li>
                                <h3><Link to={"/cusocials"} className="">Home</Link></h3>
                            </li>
                            <li>
                                <h3><Link to={"/cusocials/category/general"} className="">General</Link></h3>
                            </li>
                            <li>
                                <h3><Link to={"/cusocials/category/offer"} className="">Offer</Link></h3>
                            </li>
                            <li>
                                <h3><Link to={"/cusocials/category/video"} className="">Videos</Link></h3>
                            </li>
                            <li>
                                <h3><Link to={"/cusocials/category/announcement"} className="">Announcement</Link></h3>
                            </li>
                            <li>
                                <h3><Link to={"/cusocials/category/blog"} className="">Blog</Link></h3>
                            </li>
                        </ul>
                    </div>
                </div>
                <div className="container">
                    <div className="row">
                        <div className="col-md-12">
                            <div className=''>
                                <div className="categoryHeading">
                                    <h3 className="">{category.toUpperCase()}</h3>
                                    <div>

                                        {cuSocialsLoading ?
                                            <>
                                                <div className="text-center">Loading...</div>
                                            </>
                                            :
                                            <>
                                                <div>
                                                    {cuSocials.length > 0 ?
                                                        <>
                                                            <div className="row">
                                                                {cuSocials.map((row, index) =>
                                                                    <div className="col-md-4 col-sm-6 mb-5" key={row.id}>
                                                                        <div className="text-light categoryBox">
                                                                            <div className="categoryBoxImage">
                                                                                {category === 'video' ?
                                                                                    <div onClick={() => showVideoModal(row)}>
                                                                                    <Player fluid={true} poster={publicPath + "/assets/images/home/01.jpg"}
                                                                                        playsInline
                                                                                        src={row.youtube_url}>
                                                                                        <BigPlayButton position="center" />
                                                                                        <ControlBar disableCompletely={true} />
                                                                                    </Player>
                                                                                    </div>
                                                                                    :
                                                                                    <LazyLoadImage alt="" src={row.featured_image ? row.media_s3_base_path + row.featured_image : publicPath + '/assets/images/newsfeed.jpeg'} className="img-fluid w-100" />
                                                                                }
                                                                            </div>
                                                                            <div className="categoryComponentsDetails">
                                                                                <h5>
                                                                                    <CharactersLimitComponent isShowbutton={false} content={row.title} limit={28} />
                                                                                </h5>
                                                                                <p><CharactersLimitComponent isShowbutton={false} content={row.medium_description} limit={90} /></p>

                                                                                <Link to={`/cusocials/${row.slug}`} className="blogBtn">Read More</Link>

                                                                                <SocialShareButtons data={row}/>

                                                                            </div>
                                                                        </div>
                                                                    </div>
                                                                )}
                                                            </div>
                                                        </>
                                                        :
                                                        <><div className="text-center">No data available.</div></>
                                                        }
                                                        <VideoPopUp show={isVideoModalShow} onHide={closeVideoModal} data={activeVideoData}  />
                                                </div>
                                            </>
                                        }


                                    </div>
                                </div>
                            </div>
                        </div>

                    </div>
                </div>

            </div>
        </div>
    );
}
export default CuSocialPage;
