import { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import Spinner from 'react-bootstrap/Spinner';
import { useAuthHeader } from 'react-auth-kit';
import { API_ENDPOINT } from "../../common/Constants";


function WelcomeAgentSection() {
    const authHeader = useAuthHeader();

    let publicPath = process.env.PUBLIC_URL;

    const [dashboardStatsLoader, setDashboardStatsLoader] = useState(false);

    const [dashboardStats, setDashboardStats] = useState(null);

    useEffect(() => {
        fetchDashboardStatsData();
    }, []);

    /* SALES LIST */
    async function fetchDashboardStatsData() {
        dashboardStatsLoader(true);
        
        try {
            const res = await axios.post(API_ENDPOINT + "orgzit/welcomeinfo", '', { headers: { Authorization: authHeader() } });
            dashboardStatsLoader(false);
            setDashboardStats(res.data.data);

        } catch (error) {
            console.error("error ", error);
            dashboardStatsLoader(false);
        }
    }
    
    return (
        <>            
            <div className="welcomeContent">
                <h3>Welcome Agent</h3>
                <p>Since your last login on the system.</p>
                <ul>
                    <li>
                        <Link to={"#"}>Appointments of this Month  {!dashboardStatsLoader ?
                        <div className="text-center font-weight-bold">{dashboardStats ? dashboardStats.totalAppointmentOfMonth : '0'} </div>
                        : <div style={{fontSize:'12px',text:'center'}}><Spinner as="span" animation="grow" size="sm" role="status" aria-hidden="true" /> Loading...</div>}</Link>
                    </li>
                    <li>
                        <Link to={"#"}>Appointments of this Week <Link to={"#"}>Appointments of this Month  {!dashboardStatsLoader ?
                        <div className="text-center font-weight-bold">{dashboardStats ? dashboardStats.totalAppointmentOfWeek : '0'} </div>
                        : <div style={{fontSize:'12px',text:'center'}}><Spinner as="span" animation="grow" size="sm" role="status" aria-hidden="true" /> Loading...</div>}</Link></Link>
                    </li>
                    <li>
                        <Link to={"#"}>Meetings of this Month</Link>
                    </li>
                </ul>
            </div>                         
        </>
    );
}

export default WelcomeAgentSection;
