import { useEffect, useState } from "react";
import { Link } from 'react-router-dom';
import axios from "axios";
// import { AnimationOnScroll } from 'react-animation-on-scroll';
import AnimationOnScroll from '../AnimationOnScroll';
import { LazyLoadImage } from 'react-lazy-load-image-component';
import 'react-lazy-load-image-component/src/effects/blur.css';

import Loader from "../Loader";


function HomeProjectsComponent() {
    let publicPath = process.env.PUBLIC_URL;
    const [projectsLoading, setProjectsLoading] = useState(false);
    const [projects, setProjects] = useState([]);

    async function fetchProject() {
        setProjectsLoading(true);
        try {
            const res = await axios.get(publicPath + "/assets/data/projects.json");
            if (res.data) {
                setProjectsLoading(false);
                let featuredProjects = res.data.filter(obj => ["skylandistanbul", "alya4mevsim", "nisantasi", "acar-blu"].includes(obj.slug))
                setProjects(featuredProjects);
            }
        } catch (error) {
            console.error("error ", error);
            setProjectsLoading(false);
        }
    }

    useEffect(() => {
        window.scrollTo(0, 0);
        fetchProject();
    }, []);

    if (projectsLoading)
        return ("Please wait...");

    return (
        <>
            <div className="row">
                {projects && projects.map((row, i) =>
                    <div className="col-md-6" key={i}>
                        <div className="projectcard ">
                            <AnimationOnScroll animateIn="animate__zoomIn">
                                    <div className="imgproject">
                                        {/* {row.banner_images.length > 0 ?
                                    <img src={publicPath + row.banner_images[0]} alt={row.title} className="img-fluid" />
                                    :
                                    <img src={publicPath + "/assets/images/home/01.jpg"} alt={row.title} className="img-fluid" />
                                } */}

                                        {row.banner_images.length > 0 ?
                                            <LazyLoadImage
                                                effect="blur"
                                                src={publicPath + row.banner_images[0]}
                                                placeholderSrc={publicPath + "/assets/images/thumbnail.png"}
                                                className="img-fluid"
                                                visibleByDefault={true}
                                                alt={row.title} />
                                            :
                                            <LazyLoadImage
                                                effect="blur"
                                                src={publicPath + "/assets/images/home/01.jpg"}
                                                placeholderSrc={publicPath + "/assets/images/thumbnail.png"}
                                                className="img-fluid"
                                                visibleByDefault={true}
                                                alt={row.title} />
                                        }

                                    </div>
                            </AnimationOnScroll>
                            <div className="projectcardContent">
                                <div className="d-flex justify-content-between">
                                    <h4><Link to={`/projects/${row.slug}`}>{row.title}</Link></h4>
                                    <h4>{row.currency}{row.price_min}-{row.currency}{row.price_max}</h4>
                                </div>
                                <div className="d-flex justify-content-between">
                                    <p><svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="#B8532E" className="bi bi-geo-alt" viewBox="0 0 16 16">
                                        <path d="M12.166 8.94c-.524 1.062-1.234 2.12-1.96 3.07A31.493 31.493 0 0 1 8 14.58a31.481 31.481 0 0 1-2.206-2.57c-.726-.95-1.436-2.008-1.96-3.07C3.304 7.867 3 6.862 3 6a5 5 0 0 1 10 0c0 .862-.305 1.867-.834 2.94zM8 16s6-5.686 6-10A6 6 0 0 0 2 6c0 4.314 6 10 6 10z" />
                                        <path d="M8 8a2 2 0 1 1 0-4 2 2 0 0 1 0 4zm0 1a3 3 0 1 0 0-6 3 3 0 0 0 0 6z" />
                                    </svg>{row.city}, {row.country}</p>
                                    <p className="progressStage"> <img src={publicPath + "/assets/images/projectstage.svg"} width={20} alt="" /> {row.project_stage}%</p>
                                </div>
                                <p className="projectCardPara">{row.sub_title}</p>
                            </div>
                        </div>
                    </div>
                )}
            </div>
        </>
    );
}

export default HomeProjectsComponent;
