import {
    FacebookShareButton,
    FacebookIcon,
    WhatsappShareButton,
    WhatsappIcon,
    LinkedinShareButton,
    LinkedinIcon,
    TelegramShareButton,
    TelegramIcon,
    EmailShareButton,
    EmailIcon,
} from 'next-share';

function SocialShareButtons(props) {
    let publicPath = process.env.PUBLIC_URL;

    if(!props.data)
    return "";

    return (
        <>
            <div className="text-center my-2">
                Share it
                &nbsp;
                <FacebookShareButton
                    url={publicPath + '/cusocials/category/' + props.data.slug}
                    quote={props.data.title}
                >
                    <FacebookIcon size={32} round />
                </FacebookShareButton>
                &nbsp;
                <LinkedinShareButton
                    url={publicPath + '/cusocials/category/' + props.data.slug}
                    quote={props.data.title}
                >
                    <LinkedinIcon size={32} round />
                </LinkedinShareButton>
                &nbsp;
                <WhatsappShareButton
                    url={publicPath + '/cusocials/category/' + props.data.slug}
                    title={props.data.title}
                >
                    <WhatsappIcon size={32} round />
                </WhatsappShareButton>
                &nbsp;
                <TelegramShareButton
                    url={publicPath + '/cusocials/category/' + props.data.slug}
                    title={props.data.title}
                >
                    <TelegramIcon size={32} round />
                </TelegramShareButton>
                &nbsp;
                <EmailShareButton
                    url={publicPath + '/cusocials/category/' + props.data.slug}
                    subject={props.data.title}
                    body={props.data.long_description}
                >
                    <EmailIcon size={32} round />
                </EmailShareButton>

            </div>
        </>
    );

}

export default SocialShareButtons;
