import { useEffect, useState } from 'react';
import axios from 'axios';
import Spinner from 'react-bootstrap/Spinner';


function EnquireAboutThisPropertyComponent() {

    let publicPath = process.env.PUBLIC_URL;

    const [loading, setLoading] = useState(false);
    const [formData, setFormData] = useState({ name: '', email: '', city: '', country: '', mobile_country_code: '', mobile_number: '', message: '' });
    const [formErrors, setFormErrors] = useState('');
    const [formValidationErrors, setFormValidationErrors] = useState({});

    useEffect(() => {
        setLoading(false);
    }, []);

    const onSubmit = (e) => {
        e.preventDefault()

        setLoading(true);

        axios.post('http://staging.cuengine.com/api/v7/auth', formData)
            .then((res) => {
                console.log('login res', res.status, res.data)
                if (res.status === 200) {
                    setLoading(false);

                }
            }).catch((error) => {
                setLoading(false);
                console.log("errors ### ", error.response);
                if (error.response.status === 422) {
                    console.log("errors data ", error.response.data.errors);
                    //let errorm = error.response.data.errors[0];
                    console.log("error \n", error.response.data.message)
                    setFormValidationErrors(error.response.data.errors);
                    setFormErrors(error.response.data.message);
                    setTimeout(() => {
                        setFormValidationErrors({});
                        setFormErrors('');
                    }, 5000);
                }
            })
    }


    return (
        <>
            <div className="enquireForm sticky-top">
                <h3>Enquire about this Property</h3>
                <form onSubmit={onSubmit}>

                    <div className="mb-3">
                        <label className="form-label">Name</label>
                        <input type="text" className="form-control" placeholder="Name" name="name" onChange={(e) => setFormData({ ...formData, name: e.target.value })} />
                    </div>
                    <div className="mb-3">
                        <label className="form-label">Email Address</label>
                        <input type="email" className="form-control" placeholder="Email Address" name="email" onChange={(e) => setFormData({ ...formData, email: e.target.value })} />
                    </div>
                    <div className="mb-3">
                        <label className="form-label">Phone</label>
                        <input type="text" className="form-control" placeholder="Phone" phone="phone" onChange={(e) => setFormData({ ...formData, phone: e.target.value })} />
                    </div>
                    <div className="mb-4">
                        <label className="form-label">Message</label>
                        <textarea className="form-control" placeholder="Write Your Message" name="message" onChange={(e) => setFormData({ ...formData, message: e.target.value })}></textarea>
                    </div>

                    {loading ?
                        <button className="btntheme" type="button" disabled >
                            <Spinner as="span" animation="border" size="sm" role="status" aria-hidden="true" />  Submitting...
                        </button>
                        :
                        <button className="btntheme" type="submit">Book Appointment</button>
                    }


                </form>
            </div>
        </>
    );

}

export default EnquireAboutThisPropertyComponent;
