            style={{
                position: 'absolute',
                left: '46%',
                bottom: '-55px',
                display: 'flex',
                height: '2rem',
                width: '2rem',
                background: '#fff',
                borderRadius: '50%',
                textAlign: 'center',
                padding: '8px',
                cursor: 'pointer'
            }}
