import { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { useAuthHeader } from 'react-auth-kit';
import { API_ENDPOINT } from "../../common/Constants";


function WelcomeAgentSection() {
    const authHeader = useAuthHeader();
    const [dashboardSalesStatsLoader, setDashboardSalesStatsLoader] = useState(false);    
    const [dashboardSalesStats, setDashboardSalesStats] = useState([]);
    useEffect(() => {
        fetchDashboardAppointmentStatsData();
        //console.log("dateRange",dateRange);
    }, []);

    /* SALES LIST */
    async function fetchDashboardAppointmentStatsData() {
        setDashboardSalesStatsLoader(true);
        
        try {
            const res = await axios.post(API_ENDPOINT + "orgzit/welcomeinfo", '', { headers: { Authorization: authHeader() } });
            setDashboardSalesStatsLoader(false);
            setDashboardSalesStats(res.data.data);

        } catch (error) {
            console.error("error ", error);
            setDashboardSalesStatsLoader(false);
        }
    }
    
    return (
        <>            
            <div className="welcomeContent">
                <h3>Welcome Agent</h3>
                <p>Since your last login on the system.</p>
                <ul>
                    <li>
                        <Link to={"#"}>Appointments of this Month  </Link>
                    </li>
                    <li>
                        <Link to={"#"}>Appointments of this Week</Link>
                    </li>
                    <li>
                        <Link to={"#"}>Meetings of this Month</Link>
                    </li>
                </ul>
            </div>                         
        </>
    );
}

export default WelcomeAgentSection;
