import { Player, ControlBar, BigPlayButton} from 'video-react';
import React, { useEffect, useState } from "react";

function HomeVideoComponent(){
    let publicPath = process.env.PUBLIC_URL;

    const [isAutoPlay, setIsAutoPlay] = useState(false);
    useEffect(() => {
        setTimeout( () => {
            setIsAutoPlay(true);
        },1000)
    }, []);

    return(
        <>
        <div className="col-md-6 bannerSlider">
                            <div className="">
                                <div id="carouselExampleControls" interval="10000" className="carousel slide" data-bs-ride="carousel">
                                    <div className="carousel-inner">
                                        <div className="carousel-item active ">
                                            <Player fluid={true} autoPlay={true} poster={publicPath + "/assets/images/home/01.jpg"}
                                                playsInline
                                                src={publicPath + "/assets/homevideo/1.mp4"}>
                                                    <BigPlayButton position="center" />
                                                <ControlBar disableCompletely={true} />
                                            </Player>
                                        </div>
                                        <div className="carousel-item ">
                                        <Player fluid={true} autoPlay={true}
                                                playsInline
                                                src={publicPath + "/assets/homevideo/2.mp4"}>
                                                    <BigPlayButton position="center" />
                                                <ControlBar disableCompletely={true} />
                                            </Player>
                                            {/* <video src={publicPath + "/assets/homevideo/1.mp4"} play autoplay={true} playsInline={true} width={"700"} height={"450"} /> */}
                                        </div>
                                        <div className="carousel-item ">
                                        <Player fluid={true} autoPlay={true}
                                                playsInline
                                                src={publicPath + "/assets/homevideo/3.mp4"}>
                                                    <BigPlayButton position="center" />
                                                <ControlBar disableCompletely={true} />
                                            </Player>
                                            {/* <video src={publicPath + "/assets/homevideo/2.mp4"} autoplay={true} playsInline={true} width={"700"} height={"450"} /> */}
                                        </div>
                                        <div className="carousel-item ">
                                        <Player fluid={true} autoPlay={true}
                                                playsInline
                                                src={publicPath + "/assets/homevideo/2.mp4"}>
                                                    <BigPlayButton position="center" />
                                                <ControlBar disableCompletely={true} />
                                            </Player>
                                            {/* <video src={publicPath + "/assets/homevideo/3.mp4"} autoplay={true} playsInline={true} width={"700"} height={"450"} /> */}
                                        </div>
                                    </div>
                                    <div className="carouselNav">
                                        <button className="carousel-control-prev" type="button" data-bs-target="#carouselExampleControls" data-bs-slide="prev">
                                            <span className="carousel-control-prev-icon" aria-hidden="true">
                                                <img alt="" src={publicPath + "/assets/images/left.svg"} />
                                            </span>
                                            <span className="visually-hidden">Previous</span>
                                        </button>
                                        <button className="carousel-control-next" type="button" data-bs-target="#carouselExampleControls" data-bs-slide="next">
                                            <span className="carousel-control-next-icon" aria-hidden="true">
                                                <img alt="" src={publicPath + "/assets/images/right.svg"} />
                                            </span>
                                            <span className="visually-hidden">Next</span>
                                        </button>
                                    </div>
                                </div>
                            </div>
                        </div>
        </>
    )
}
export default HomeVideoComponent;
