import React, { useEffect } from 'react';
import { useNavigate, Navigate } from 'react-router-dom';
import axios from 'axios';
import Spinner from 'react-bootstrap/Spinner';
import { useSignIn, useIsAuthenticated } from 'react-auth-kit';
import Modal from 'react-bootstrap/Modal';
import { LazyLoadImage } from 'react-lazy-load-image-component';
import 'react-lazy-load-image-component/src/effects/blur.css';

function ForgotPasswordModal(props) {
    let publicPath = process.env.PUBLIC_URL;

    const isAuthenticated = useIsAuthenticated();
    const navigate = useNavigate();
    const signIn = useSignIn();


    const [show, setShow] = React.useState(false);

    const handleClose = () =>{ setShow(false);}
    const handleShow = () => setShow(true);


    const [loading, setLoading] = React.useState(false);
    const [formData, setFormData] = React.useState({ email: '', password: '' });
    const [formErrors, setFormErrors] = React.useState('');
    const [formValidationErrors, setFormValidationErrors] = React.useState({});

    // if (isAuthenticated()) {
    //     // Redirect to dashboard
    //     console.log("valid Redirect to dashboard");
    //     return (
    //         <Navigate to="/dash" replace />
    //     );
    // }
    useEffect(() => {
        if(props.isForgotPasswordModalShow){
            handleShow();
        }
        if(!show){
            props.handleChane();
        }
    }, [props,show])

    const onSubmit = (e) => {
        e.preventDefault()
        setLoading(true);
        axios.post('http://staging.cuengine.com/api/v7/auth', formData)
            .then((res) => {
                console.log('login res', res.status, res.data)
                if (res.status === 200) {
                    if (signIn({
                        token: res.data.data.token,
                        expiresIn: res.data.data.expires_at,
                        tokenType: "Bearer",
                        authState: { user: res.data.data.user },
                        //    refreshToken: res.data.refreshToken, // Only if you are using refreshToken feature
                        //    refreshTokenExpireIn: res.data.refreshTokenExpireIn
                    })) { // Only if you are using refreshToken feature
                        // Redirect or do-something
                        navigate("/dash");
                    } else {
                        //Throw error
                        //alert("Errors \n"+res.errors[0])

                    }
                }
            }).catch((error) => {
                setLoading(false);
                console.log("errors ### ", error.response);
                if (error.response.status === 422) {
                    console.log("errors data ", error.response.data.errors);
                    //let errorm = error.response.data.errors[0];
                    console.log("error \n", error.response.data.message)
                    setFormValidationErrors(error.response.data.errors);
                    setFormErrors(error.response.data.message);
                    setTimeout(() => {
                        setFormValidationErrors({});
                        setFormErrors('');
                    }, 5000);
                }
            })
    }


    return (
        <>
        <Modal show={show} onHide={handleClose} className="SignIn" centered>

            {/* <div className="modal fade SignIn" id="SignIn" tabIndex="-1" aria-labelledby="SignInLabel" aria-hidden="true"> */}
                {/* <div className="modal-dialog modal-dialog-centered">
                    <div className="modal-content"> */}
                    <Modal.Header closeButton>
                    </Modal.Header>
                        <div className="modal-body">
                            <div className='signinImg'>                                                            
                                <div className='ChangePasswordFrom'>
                                    <h2>Change Password</h2>
                                    <p>Lorem ipsum dolor sit amet</p>
                                    <form onSubmit={onSubmit}>
                                        <div className="mb-3">
                                            {/* {loading ? 'loading...' : ''} */}
                                            {/* {formErrors} */}
                                            <ul className="list-unstyled">
                                                {formValidationErrors && Object.keys(formValidationErrors).map((error, index) => (
                                                    <li className="text-danger" key={index}>{formValidationErrors[error][0]}</li>
                                                ))}
                                            </ul>
                                        </div>
                                        <div className="mb-3">
                                            <input type="password" className="form-control" name="password" placeholder='Old Password' onChange={(e) => setFormData({ ...formData, password: e.target.value })} />
                                        </div>
                                        <div className="mb-3">
                                            <input type="password" className="form-control" name="password" placeholder='New Password' onChange={(e) => setFormData({ ...formData, password: e.target.value })} />
                                        </div>
                                        <div className="mb-3">
                                            <input type="password" className="form-control" name="password" placeholder='Confirm Password' onChange={(e) => setFormData({ ...formData, password: e.target.value })} />
                                        </div>

                                        {loading ?
                                            <button className="btn btn-primary" type="button" disabled >
                                                <Spinner as="span" animation="border" size="sm" role="status" aria-hidden="true" />  Submitting...
                                            </button>
                                            :
                                            <button className="btn btn-primary" type="submit">Submit</button>
                                        }


                                        {/* <button type="submit" className="btn btn-primary">Sign in</button> */}
                                    </form>
                                </div>
                            </div>
                        </div>

                    {/* </div>
                </div> */}
            {/* </div> */}
            </Modal>
        </>
    );
}

export default ForgotPasswordModal;
