import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import Spinner from 'react-bootstrap/Spinner';
import { useSignIn, useIsAuthenticated } from "react-auth-kit";
import Modal from "react-bootstrap/Modal";

function ForgotPasswordModal(props) {
    let publicPath = process.env.PUBLIC_URL;

    const isAuthenticated = useIsAuthenticated();
    const navigate = useNavigate();

    const handleClose = () => { 
        setShowfp(false); 
       // props.handleChanel();
        console.log("handleClose clicked ",showfp);
     }
    const handleShow = () => { 
        setShowfp(true); 
        console.log("handleShow clicked ",showfp);
    }
    const [showfp, setShowfp] = React.useState(true);

    const [loading, setLoading] = React.useState(false);
    const [formData, setFormData] = React.useState({ email: '' });
    const [formErrors, setFormErrors] = React.useState('');
    const [formValidationErrors, setFormValidationErrors] = React.useState({});

    useEffect(() => {
        handleShow();

        if (!showfp) {
        props.handleChanel();
    }
    }, [props,showfp]);


    const onSubmit = (e) => {
        e.preventDefault()
        setLoading(true);
        axios.post('http://staging.cuengine.com/api/v7/auth/forgetPassword', formData)
            .then((res) => {
                setLoading(false);
                console.log('forgetPassword res', res.status, res.data)
                if (res.status === 200) {
                    setFormErrors(res.data.message);

                    setTimeout(() => {
                        setFormErrors('');
                        navigate("/");
                    }, 5000);
                }
            }).catch((error) => {
                setLoading(false);
                console.log("errors ### ", error.response);
                if (error.response.status === 422) {
                    console.log("errors data ", error.response.data.errors);
                    //let errorm = error.response.data.errors[0];
                    console.log("error \n", error.response.data.message)
                    setFormValidationErrors(error.response.data.errors);
                    setFormErrors(error.response.data.message);
                    setTimeout(() => {
                        setFormValidationErrors({});
                        setFormErrors('');
                    }, 5000);
                }
            })
    }


    return (
        <>

            <Modal show={true} onHide={handleClose} className="forgot-password" centered>
                <Modal.Header closeButton>
                </Modal.Header>
                <div className="modal-body">
                    <div className="forgotpasswordForm">
                        <h2>Forgot Password</h2>
                        <form onSubmit={onSubmit}>
                            <div className="mb-3">
                                {formErrors}
                                <ul className="list-unstyled">
                                    {formValidationErrors && Object.keys(formValidationErrors).map((error, index) => (
                                        <li className="text-danger" key={index}>{formValidationErrors[error][0]}</li>
                                    ))}
                                </ul>
                            </div>
                            <div className="mb-3">
                                <input type="email" className="form-control" name="email" placeholder="Email Address" onChange={(e) => setFormData({ ...formData, email: e.target.value })} />
                            </div>
                            {loading ?
                                <button className="btn btn-primary" type="button" disabled >
                                    <Spinner as="span" animation="border" size="sm" role="status" aria-hidden="true" />  Submitting...
                                </button>
                                :
                                <button className="btn btn-primary" type="submit">Submit</button>
                            }

                        </form>
                    </div>
                </div>
            </Modal>
        </>
    );
}

export default ForgotPasswordModal;
