import { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { useAuthHeader } from 'react-auth-kit';
import { API_ENDPOINT } from "../../common/Constants";
import Spinner from 'react-bootstrap/Spinner';


function WelcomeAgentSection() {
    const authHeader = useAuthHeader();

    const [dashboardStatsLoader, setDashboardStatsLoader] = useState(false);

    const [dashboardStats, setDashboardStats] = useState([]);


    useEffect(() => {
        fetchAppointmentDatStats();
    }, []);

    /* SALES LIST */
    async function fetchAppointmentDatStats() {
        setDashboardStatsLoader(true);
        
        try {
            const res = await axios.post(API_ENDPOINT + "orgzit/welcomeinfo", '', { headers: { Authorization: authHeader() } });
            dashboardStatsLoader(false);
            alert(res.data);
            setDashboardStats(res.data.data);

        } catch (error) {
            console.error("error ", error);
            setDashboardStatsLoader(false);
        }
    }

    return (
        <>            
            <div className="welcomeContent">
                <h3>Welcome Agent</h3>
                <p>Since your last login on the system.</p>
                <ul>
                    <li>
                        <Link to={"#"}>Appointments of this Month {dashboardStats.totalAppointmentOfMonth} {!dashboardStatsLoader ?
                                            <div className="text-center font-weight-bold">{dashboardStats ? dashboardStats.totalAppointmentOfMonth : ''} </div>
                                            : <div style={{fontSize:'12px',text:'center'}}><Spinner as="span" animation="grow" size="sm" role="status" aria-hidden="true" /> Loading...</div>}</Link>
                    </li>
                    <li>
                        <Link to={"#"}>Appointments of this Week {!dashboardStatsLoader ?
                                            <div className="text-center font-weight-bold">{dashboardStats ? dashboardStats.totalAppointmentOfWeek : ''} </div>
                                            : <div style={{fontSize:'12px',text:'center'}}><Spinner as="span" animation="grow" size="sm" role="status" aria-hidden="true" /> Loading...</div>}</Link>
                    </li>
                    <li>
                        <Link to={"#"}>Meetings of this Month {!dashboardStatsLoader ?
                                            <div className="text-center font-weight-bold">{dashboardStats ? dashboardStats.TotalMeetingsOfMonth : ''} </div>
                                            : <div style={{fontSize:'12px',text:'center'}}><Spinner as="span" animation="grow" size="sm" role="status" aria-hidden="true" /> Loading...</div>}</Link>
                    </li>
                </ul>
            </div>                         
        </>
    );

}

export default WelcomeAgentSection;
