import React, { useState, useEffect } from "react";
import { Link } from 'react-router-dom';
import Moment from 'react-moment';
import axios from "axios";
import { LazyLoadImage } from 'react-lazy-load-image-component';

import { API_ENDPOINT } from "../../common/Constants";
import CharactersLimitComponent from "../CharactersLimitComponent";


function NewsFeedHome() {
    let publicPath = process.env.PUBLIC_URL;

    const [newsFeedsLoading, setNewsFeedsLoading] = useState(false);
    const [newsFeeds, setNewsFeeds] = useState([]);

    useEffect(() => {
        fetchData();
    }, []);

    async function fetchData() {
        setNewsFeedsLoading(true);
        try {
            const res = await axios.get(API_ENDPOINT + "cusocial/featuredHome");
            if (res.data.data)
                setNewsFeeds(res.data.data);

            setNewsFeedsLoading(false);

        } catch (error) {
            console.error("error ", error);
            setNewsFeedsLoading(false);
        }
    }

    return (
        <>
            <div className="row">
                {console.log('newsFeeds ', newsFeedsLoading, newsFeeds)}
                {newsFeedsLoading ?
                    <>
                        <div className="col-md-12 text-center">Loading...</div>
                    </>
                    :
                    <>

                        {newsFeeds.length > 0 && newsFeeds.map((row, index) =>
                            <div className="col-md-6" key={row.id}>
                                <div className="newsfeedcard">
                                    <div className="row">
                                        <div className="col-md-5">
                                            {row.featured_image ?
                                                <LazyLoadImage alt="" src={row.media_s3_base_path + row.featured_image} className="img-fluid" />
                                                :
                                                <LazyLoadImage alt="" src={publicPath + '/assets/images/newsfeed.jpeg'} className="img-fluid" />
                                            }

                                            {/* <img alt="" src={publicPath + row.featured_image} className="img-fluid" /> */}
                                        </div>
                                        <div className="col-md-7">
                                            <div className="newsfeedDetail">
                                                <Link to={`/cusocials/${row.slug}`} ><h5>
                                                    <CharactersLimitComponent isShowbutton={false} content={row.title} limit={100} />
                                                </h5></Link>
                                                <p>
                                                    <CharactersLimitComponent isShowbutton={false} content={row.medium_description} limit={220} />
                                                </p>
                                                <p><Moment fromNow>{row.created_at}</Moment></p>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        )}
                        {newsFeeds.length < 1 ? <><div className="col-md-12 text-center">No data available.</div></> : <></>}

                    </>
                }


            </div>
        </>
    );
}

export default NewsFeedHome;
