import React, { useState, useEffect } from "react";
import Moment from 'react-moment';
import axios from "axios";
import { LazyLoadImage } from 'react-lazy-load-image-component';

let publicPath = process.env.PUBLIC_URL;

function NewsFeedHome() {
    const [newsFeeds, setNewsFeeds] = useState([]);
    useEffect(() => {
        fetchData();
    }, []);

    async function fetchData() {
        try {
            const res = await axios.get(publicPath + "/assets/data/newsfeeds.json");
            setNewsFeeds(res.data);
        } catch (error) {
            console.error("error ", error);
        }
    }

    return (
        <>
            <div className="row">
                {newsFeeds.length > 0 && newsFeeds.map((row, index) =>

                    <div className="col-md-6" key={row.id + 'nf'}>
                        <div className="newsfeedcard">
                            <div className="row">
                                <div className="col-md-5">
                                    <LazyLoadImage alt="" src={publicPath + row.featured_image} className="img-fluid" />
                                    {/* <img alt="" src={publicPath + row.featured_image} className="img-fluid" /> */}
                                </div>
                                <div className="col-md-7">
                                    <div className="newsfeedDetail">
                                        <h5>{row.title}</h5>
                                        <h6>{row.description}</h6>
                                        <p><Moment fromNow>{row.created_at}</Moment></p>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                )}

            </div>
        </>
    );
}

export default NewsFeedHome;
