import React, { useEffect, useState } from "react";
import axios from "axios";
import { useAuthHeader } from 'react-auth-kit';
import { API_ENDPOINT } from "../../common/Constants";
import DataTable from 'react-data-table-component';


function InvitationsDashPage() {

  const authHeader = useAuthHeader();
  const [invitationsLoader, setInvitationsLoader] = useState(false);
  const [invitations, setInvitations] = useState([]);
  const [page, setPage] = useState(1);
  const countPerPage = 3;

  useEffect(() => {
    fetchInvitationData();
  }, []);

  async function fetchInvitationData() {
    setInvitationsLoader(true);
    let postData = {
      test: null,
      start_date: '2022-02-01T00:00:00.000Z',
      end_date: '2022-02-28T00:00:00.000Z'
    };

    try {
      const res = await axios.post(API_ENDPOINT + "orgzit/payplans", postData, { headers: { Authorization: authHeader() } });
      setInvitationsLoader(false);
      setInvitations(res.data.data);

    } catch (error) {
      console.error("error ", error);
      setInvitationsLoader(false);
    }
  }

  const columns = React.useMemo(() => [


        { name: "id", selector: "id" },
        { name: "SalesPerson", selector: "SalesPerson" },
        { name: "ProjectName", selector: "ProjectName" },
        { name: "PaymentType", selector: "PaymentType" },
        { name: "PaymentStatus", selector: "PaymentStatus" },
        { name: "PaymentCurrency", selector: "PaymentCurrency" },
        { name: "ExpectedPaymentDate", selector: "ExpectedPaymentDate" },
        { name: "ExpectedPaymentAmount", selector: "ExpectedPaymentAmount" },

  ], []);

  console.log("invitations ",invitations);
  const data = React.useMemo(() => invitations, []);
  return <DataTable
        title="Employees"
        columns={columns}
        data={invitations}
        highlightOnHover
        pagination
        paginationServer
        paginationTotalRows={invitations.length}
        paginationPerPage={countPerPage}
        paginationComponentOptions={{
          noRowsPerPage: true
        }}
        onChangePage={page => setPage(page)}
      />;
}

export default InvitationsDashPage;
