import React, { useEffect, useRef, useState } from 'react';
import axios from "axios";
import { LazyLoadImage } from 'react-lazy-load-image-component';

function ContactUsComponent() {
    let publicPath = process.env.PUBLIC_URL;
    const [contactusLoading, setContactUsLoading] = useState(false);
    const [contactUs, setContactUs] = useState([]);

    async function fetchContactUs() {
        setContactUsLoading(true);
        try {
            const res = await axios.get(publicPath + "/assets/data/ContactUs.json");
            if (res.data) {
                setContactUsLoading(false);
                setContactUs(res.data);
            }
        } catch (error) {
            console.error("error ", error);
            setContactUsLoading(false);
        }
    }

    useEffect(() => {
        window.scrollTo(0, 0);
        fetchContactUs();
    }, []);
    return (
        <>
            <div className="locationCard">
                <div className="container">
                    <div className="row justify-content-center">
                        {contactUs && contactUs.map((row, index) => {
                            return (
                                <div className="col-md-4" key={index}>
                                    <div className="locationCardData">
                                        <h4>INDIA</h4>
                                        <div className="locationCardDetails">
                                            <div className="d-flex justify-content-between">
                                            <div className="col-1">
                                                        <LazyLoadImage alt="" src={publicPath + row.location_logo} className="img-fluid"/>
                                                    </div>
                                                <div className="col-9">
                                                    <p className="locationCardDataPara">{row.location_address}</p>
                                                </div>
                                            </div>
                                            <div className="d-flex justify-content-between">
                                            <div className="col-1">
                                                        <LazyLoadImage alt="" src={publicPath + row.contact_logo} className="img-fluid"/>
                                                    </div>
                                                <div className="col-9">
                                                    <p className="locationCardDataPara">{row.contact_numbder}</p>
                                                </div>
                                            </div>
                                            <div className="d-flex justify-content-between">
                                                <div className="col-1">
                                                        <LazyLoadImage alt="" src={publicPath + row.email_logo} className="img-fluid"/>
                                                    </div>
                                                <div className="col-9">
                                                    <p className="locationCardDataPara">{row.email_id}</p>
                                                    <a className="locationContactIcon">
                                                        <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-geo-alt" viewBox="0 0 16 16">
                                                            <path d="M12.166 8.94c-.524 1.062-1.234 2.12-1.96 3.07A31.493 31.493 0 0 1 8 14.58a31.481 31.481 0 0 1-2.206-2.57c-.726-.95-1.436-2.008-1.96-3.07C3.304 7.867 3 6.862 3 6a5 5 0 0 1 10 0c0 .862-.305 1.867-.834 2.94zM8 16s6-5.686 6-10A6 6 0 0 0 2 6c0 4.314 6 10 6 10z" />
                                                            <path d="M8 8a2 2 0 1 1 0-4 2 2 0 0 1 0 4zm0 1a3 3 0 1 0 0-6 3 3 0 0 0 0 6z" />
                                                        </svg>
                                                    </a>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                                )
                            })}
                    </div>
                </div>
            </div>
        </>
    )
}
export default ContactUsComponent;
