import React, { useEffect, useRef, useState } from 'react';
import "react-multi-carousel/lib/styles.css";
import { Link } from 'react-router-dom';
import Moment from 'react-moment';
import axios from "axios";
import "video-react/dist/video-react.css";
import { LazyLoadImage } from 'react-lazy-load-image-component';
import { useIsAuthenticated } from 'react-auth-kit';

import EventPage from "../components/update/EventPage"
import BlogPage from "../components/update/BlogPage"
import OfferPage from "../components/update/OfferPage"
import YoutubeVideoPage from "../components/update/YoutubeVideoPage"
import GeneralPage from "../components/update/GeneralPage"
import AnnouncementPage from "../components/update/AnnouncementPage"

function CuSocialPage(props) {
    let publicPath = process.env.PUBLIC_URL;
    const isAuthenticated = useIsAuthenticated();

    const [updatesLoading, setUpdatesLoading] = useState(false);
    const [updates, setUpdates] = useState([]);

    async function fetchUpdates() {
        setUpdatesLoading(true);
        try {
            const res = await axios.get(publicPath + "/assets/data/updates.json");
            if (res.data) {
                setUpdatesLoading(false);
                setUpdates(res.data);
            }
        } catch (error) {
            console.error("error ", error);
            setUpdatesLoading(false);
        }
    }

    useEffect(() => {
        window.scrollTo(0, 0);
        fetchUpdates();
    }, []);

    const onPrevClick = () => {
        carouselRef.current.prev();
    };
    const onNextClick = () => {
        carouselRef.current.next();
    };

    const responsive = {
        superLargeDesktop: {
            // the naming can be any, depends on you.
            breakpoint: { max: 4000, min: 3000 },
            items: 4
        },
        desktop: {
            breakpoint: { max: 3000, min: 1024 },
            items: 3
        },
        tablet: {
            breakpoint: { max: 1024, min: 464 },
            items: 2
        },
        mobile: {
            breakpoint: { max: 464, min: 0 },
            items: 1
        }
    };

    const carouselRef = useRef(null);

    return (
        <div className='mt-70'>
            <div className='UpdatesSection'>
                <div className="bannerAboutUs">
                    <img src={publicPath + "/assets/images/bannerimages/cusocialbanner.jpg"} className="img-fluid w-100" />

                </div>
                <div className='container'>
                    <div className='bannerAboutUs'>
                        <h1>CuSocial</h1>
                    </div>
                    <div className="cuSocialMainTabs">
                        <ul className=" ">
                            <li>
                                <h3><Link to={"/cusocials"} className="">Home</Link></h3>
                            </li>
                            {/* <li className="">
                                <h3><Link to={"/cusocials/category/general"} className="">General</Link></h3>
                            </li> */}
                             {isAuthenticated() ?
                            <li className="">
                                <h3><Link to={"/cusocials/category/offers"} className="">Offers</Link></h3>
                            </li>
                            :null
                            }

                            {/* <li className="">
                                <h3><Link to={"/cusocials/category/video"} className="">Videos</Link></h3>
                            </li>
                            <li className="">
                                <h3><Link to={"/cusocials/category/announcement"} className="">Announcement</Link></h3>
                            </li> */}
                            <li className="">
                                <h3 className='text-md-end'><Link to={"/cusocials/category/blog"} className="">Blog</Link></h3>
                            </li>
                        </ul>
                    </div>
                </div>
                <div className="container">
                    <div className="row">
                        <div className="col-md-8">
                            <div id="general">
                                <GeneralPage />
                            </div>
                        </div>

                        <div className="col-md-12" id="offer">
                            <OfferPage />
                        </div>
                        {/* <div className='col-md-12' id="announcement">
                                <div className='announcementContent'>
                                    <h2>Announcement</h2>
                                    <div>
                                        <AnnouncementPage />
                                    </div>
                                </div>
                            </div> */}
                        {/* <div className="col-md-4">
                            <div className="row">
                                <div className='col-12'>
                                    <div className="stagicContent p-1">
                                        <img src={publicPath + "/assets/images/missionvision.png"} className="img-fluid " />
                                    </div>
                                </div>
                                <div className='col-12'>
                                    <div className="latestContent p-1">
                                        <img src={publicPath + "/assets/images/missionvision.png"} className="img-fluid " />
                                    </div>
                                </div>
                            </div>
                        </div> */}
                    </div>
                </div>
                <div id="blog">
                    <BlogPage />
                </div>
                {/* <div id="videos">
                    <YoutubeVideoPage />
                </div> */}
            </div>
        </div>
    );
}
export default CuSocialPage;
