import React, { useEffect, useRef, useState } from 'react';
import "react-multi-carousel/lib/styles.css";
import { Link } from 'react-router-dom';
import Moment from 'react-moment';
import EventPage from "../components/update/EventPage"
import BlogPage from "../components/update/BlogPage"
import OfferPage from "../components/update/OfferPage"
import YoutubeVideoPage from "../components/update/YoutubeVideoPage"
import axios from "axios";
import "video-react/dist/video-react.css";
import GeneralPage from "../components/update/GeneralPage"
import SimpleSwiper from "../components/update/SimpleSwiper"
import { LazyLoadImage } from 'react-lazy-load-image-component';


function Updates(props) {
    let publicPath = process.env.PUBLIC_URL;
    const [updatesLoading, setUpdatesLoading] = useState(false);
    const [updates, setUpdates] = useState([]);

    async function fetchUpdates() {
        setUpdatesLoading(true);
        try {
            const res = await axios.get(publicPath + "/assets/data/updates.json");
            if (res.data) {
                setUpdatesLoading(false);
                setUpdates(res.data);
            }
        } catch (error) {
            console.error("error ", error);
            setUpdatesLoading(false);
        }
    }

    useEffect(() => {
        window.scrollTo(0, 0);
        fetchUpdates();
    }, []);

    const onPrevClick = () => {
        carouselRef.current.prev();
    };
    const onNextClick = () => {
        carouselRef.current.next();
    };

    const responsive = {
        superLargeDesktop: {
            // the naming can be any, depends on you.
            breakpoint: { max: 4000, min: 3000 },
            items: 4
        },
        desktop: {
            breakpoint: { max: 3000, min: 1024 },
            items: 3
        },
        tablet: {
            breakpoint: { max: 1024, min: 464 },
            items: 2
        },
        mobile: {
            breakpoint: { max: 464, min: 0 },
            items: 1
        }
    };

    const carouselRef = useRef(null);

    return (
        <div className=''>
            <div className='UpdatesSection'>
                <div className="bannerAboutUs">
                    {/* <img src={publicPath + "/assets/images/aboutus.jpg"} className="img-fluid w-100" /> */}
                    <h1>CuSocial</h1>
                </div>
                <div className='container'>
                    {/* <div className='updateBox'>
                        <div className='row'>
                            {updatesLoading ? 'please wait...' : ''}
                            {updates  &amp;  &amp;  updates.map((row, index) => {
                                return (
                                    <div className='col-md-3' key={index}>
                                        <div className='updateData'>
                                            <div className='updatesImage'>
                                                <LazyLoadImage alt="" src={publicPath + row.featured_image} className="img-fluid" />
                                            </div>
                                            <div className='updateContent'>
                                                <h5>{row.title}</h5>
                                                <p>{row.small_description}</p>
                                                <p><Moment format="YYYY/MM/DD">{row.started_at}</Moment> - <Moment format="YYYY/MM/DD">{row.ended_at}</Moment> T &amp; amp;C</p>
                                            </div>
                                        </div>
                                    </div>
                                )
                            })}
                        </div>
                    </div> */}
                    <nav id="navbar-example2" className="navbar navbar-light  px-3 CusocialHeading">
                        <ul className="nav nav-pills">
                            <li className="nav-item">
                                <a className="nav-link" href="">General</a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link" href="">Offer</a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link" href="">Announcement</a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link" href="">Blog</a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link" href="">Videos</a>
                            </li>
                        </ul>
                    </nav>
                    <div className="row">
                        <div className="col-md-8 px-0">
                            < GeneralPage />
                            <div className="row mx-0 px-0">
                                <div className="col-md-12">
                                    <OfferPage />
                                </div>
                                <div className='col-md-12 ' >
                                    <div className='announcementContent'>
                                        <h2>Announcement</h2>
                                        <div>
                                            <SimpleSwiper />
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="col-md-4">
                            <div className="row mx-0 px-0">
                                <div className='col-12'>
                                    <div className="stagicContent p-1">
                                        <img src={publicPath + "/assets/images/stagic.png"} className="img-fluid w-100" />
                                    </div>
                                </div>
                                <div className='col-12'>
                                    <div className="latestContent p-1">
                                        <img src={publicPath + "/assets/images/latest.png"} className="img-fluid w-100" />
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div>
                        <BlogPage />
                    </div>
                    <div>
                        <YoutubeVideoPage />
                    </div>
                </div>
            </div>
        </div>
    );
}
export default Updates;
