import { useState, useEffect } from "react";
import axios from "axios";
import { useAuthHeader } from 'react-auth-kit';
import DateRangePicker from '@wojtekmaj/react-daterange-picker';
import 'react-calendar/dist/Calendar.css';
import { API_ENDPOINT } from "../../common/Constants";

function MonthlyPaymentPlanList() {
    const authHeader = useAuthHeader();
    
    const [state, setState] = useState([
    {
      startDate: new Date(),
      endDate: addDays(new Date(), 7),
      key: "selection"
    }]);
    const [dashboardSalesStatsLoader, setDashboardSalesStatsLoader] = useState(false);
    const [dashboardSalesStats, setDashboardSalesStats] = useState([]);

    useEffect(() => {
        fetchDashboardSalesStatsData();
    }, []);

    /* SALES LIST */
    async function fetchDashboardSalesStatsData() {
        setDashboardSalesStatsLoader(true);
        let postData = {
            test: null
        };

        try {
            const res = await axios.post(API_ENDPOINT + "orgzit/payplans", postData, { headers: { Authorization: authHeader() } });
            setDashboardSalesStatsLoader(false);
            setDashboardSalesStats(res.data.data);

        } catch (error) {
            console.error("error ", error);
            setDashboardSalesStatsLoader(false);
        }
    }
    return (
        <>
            <div className="col-2 p-3 mb-2 w-100 bg-light text-black text-left border admDashBoardSalesMonthList">

                <div className="tableFilterPanel">
                    <h2>Monthly Payment Plans List</h2>
                    <span className="wiseFilter">
                        {/*<DateRangePicker onChange={onChange} value={value} />*/}
                        <DateRangePicker
                            onChange={(item) => setState([item.selection])}
                            showSelectionPreview={true}
                            moveRangeOnFirstSelection={false}
                            months={1}
                            ranges={state}
                            direction="horizontal"
                          />
                    </span>                    
                </div>
                <div className="table-responsive">
                    <table className="table table-sm">
                        <thead>
                            <tr><td colSpan="6"></td></tr>
                            <tr>
                                <td>#</td>
                                <td>Project Name</td>
                                <td>Sales Person</td>
                                <td>Payment Type</td>
                                <td>Payment Status</td>
                                <td>Currency</td>
                                <td>Expected Payment Amount</td>
                                <td>Expected Payment Date</td>                                                                
                            </tr>
                        </thead>
                        <tbody>
                            {dashboardSalesStatsLoader ? (<tr><td colSpan="6" align="center">Loading...</td></tr>) : null}
                            {dashboardSalesStats.length > 0 && dashboardSalesStats.map((row, index) =>
                                <tr key={index}>
                                    <td>{row.id}</td>
                                    <td>{row.ProjectName}</td>
                                    <td>{row.SalesPerson}</td>
                                    <td>{row.PaymentType}</td>
                                    <td>{row.PaymentStatus}</td>
                                    <td>{row.PaymentCurrency}</td>
                                    <td>{row.ExpectedPaymentAmount}</td>
                                    <td>{row.ExpectedPaymentDate}</td>
                                </tr>
                            )}
                        </tbody>
                    </table>
                </div>
            </div>
        </>
    );
}

export default MonthlyPaymentPlanList;
