import { useState, useEffect } from "react";
import axios from "axios";

function DashboardStats() {
    const [dashboardStatsLoader, setDashboardStatsLoader] = useState(false);
    const [dashboardStats, setDashboardStats] = useState(null);


    useEffect(() => {

        fetchDashboardStatsData();
    }, []);

    async function fetchDashboardStatsData() {
        setDashboardStatsLoader(true);
        try {
            const res = await axios.get("http://localhost:8000/api/orgzit");
            setDashboardStatsLoader(false);
            setDashboardStats(res.data.data);

        } catch (error) {
            console.error("error ", error);
            setDashboardStatsLoader(false);
        }
    }

    return (
        <>
            <div className="row mt-5">
                <div className="col-2 p-3 mb-2 bg-warning text-white  text-center border">
                    <div className=""><strong>Total Agents</strong></div>
                    {!dashboardStatsLoader ?
                        <div className="admDashBoardCounters">{dashboardStats ? dashboardStats.TotalAgents : ''} </div>
                        : 'Loading...'}
                </div>
                <div className="col-2 p-3 mb-2 bg-info text-white  text-center border">
                    <div className=""><strong>Total Appointments</strong></div>
                    {!dashboardStatsLoader ?
                        <div className="admDashBoardCounters">{dashboardStats ? dashboardStats.TotalAppointments : ''} </div>
                        : 'Loading...'}
                </div>
                <div className="col-2 p-3 mb-2 bg-light text-black  text-center border">
                    <div className=""><strong>Total Meetings</strong></div>
                    {!dashboardStatsLoader ?
                        <div className="admDashBoardCounters">{dashboardStats ? dashboardStats.TotalMeetings : ''} </div>
                        : 'Loading...'}
                </div>
                <div className="col-2 p-3 mb-2 bg-dark text-white  text-center border">
                    <div className=""><strong>Total Oppotunities</strong></div>
                    {!dashboardStatsLoader ?
                        <div className="admDashBoardCounters">{dashboardStats ? dashboardStats.TotalOppotunitie : ''} </div>
                        : 'Loading...'}
                </div>
                <div className="col-2 p-3 mb-2 bg-warning text-white text-center border">
                    <div className=""><strong>Numbers Of Sales Today</strong></div>
                    {!dashboardStatsLoader ?
                        <div className="admDashBoardCounters">{dashboardStats ? dashboardStats.TotalSales : ''} </div>
                        : 'Loading...'}
                </div>

                <div className="col-2 p-3 mb-2 bg-warning text-white text-center border admDashBoardCountersMonthWise">
                    <div className=""><strong>Total Agents  Of The Month</strong></div>
                    {!dashboardStatsLoader ?
                        <div className="admDashBoardCounters">{dashboardStats ? dashboardStats.TotalAgentMonth : ''} </div>
                        : 'Loading...'}
                </div>

                <div className="col-2 p-3 mb-2 bg-info text-white text-center border admDashBoardCountersMonthWise">
                    <div className=""><strong>Total Sales Of The Month</strong></div>
                    {!dashboardStatsLoader ?
                        <div className="admDashBoardCounters">{dashboardStats ? dashboardStats.TotalSalesMonth : ''} </div>
                        : 'Loading...'}
                </div>
            </div>
        </>
    );

}

export default DashboardStats;
