import { useState, useEffect } from "react";
import axios from "axios";
import { useAuthHeader } from 'react-auth-kit';
import DateRangePicker from '@wojtekmaj/react-daterange-picker';
import 'react-calendar/dist/Calendar.css';
import { API_ENDPOINT } from "../../common/Constants";
import Moment from 'react-moment';
import moment from 'moment';

function MonthlySalesList() {
    const authHeader = useAuthHeader();
    const startOfMonth = moment().startOf('month').format('YYYY-MM-DD');
    const endOfMonth   = moment().endOf('month').format('YYYY-MM-DD');
    const [dateRange, onChangeDateRange] = useState([new Date(startOfMonth), new Date(endOfMonth)]);
    const [dashboardSalesStatsLoader, setDashboardSalesStatsLoader] = useState(false);
    const [dashboardSalesStats, setDashboardSalesStats] = useState([]);

    useEffect(() => {
        fetchDashboardSalesStatsData();
    }, []);

    /* SALES LIST */
    async function fetchDashboardSalesStatsData() {
        setDashboardSalesStatsLoader(true);
        let postData = {
            test: null,
            start_date: dateRange[0],
            end_date: dateRange[1]
        };
        try {
            const res = await axios.post(API_ENDPOINT + "orgzit/sales", postData, { headers: { Authorization: authHeader() } });
            setDashboardSalesStatsLoader(false);
            setDashboardSalesStats(res.data.data);

        } catch (error) {
            console.error("error ", error);
            setDashboardSalesStatsLoader(false);
        }
    }
    return (
        <>
            <div className="col-2 p-3 mb-2 w-100 bg-light text-black text-left border admDashBoardSalesMonthList">

                <div className="tableFilterPanel">
                    <h2>Monthly Sales List</h2>
                    <span className="wiseFilter">
                    <DateRangePicker onChange={onChangeDateRange} value={dateRange} />
                    </span>
                    <span className="wiseFilter">
                        <button type="button" className="btn btn-primary" onClick={fetchDashboardSalesStatsData}>Go</button>
                    </span>  
                </div>
                <div className="table-responsive ">
                    <table className="table table-sm ">
                        <thead>
                            <tr><td colSpan="6"></td></tr>
                            <tr>
                                <td>#</td>
                                <td>Sold On Date</td>
                                <td>Sales Location</td>
                                <td align="right">Sales Price</td>
                                <td align="right">Sales Status</td>
                                <td align="right">Grand Total</td>
                            </tr>
                        </thead>
                        <tbody>
                            {dashboardSalesStatsLoader ? (<tr><td colSpan="6" align="center">Loading...</td></tr>) : null}
                            {dashboardSalesStats.length > 0 && dashboardSalesStats.map((row, index) =>
                                <tr key={index}>
                                    <td>{index + 1}</td>
                                    <td>{row.agent_name}</td>
                                    <td>{row.customer_name}</td>
                                    <td align="right">{row.agent_commission_usd} (<strong>{row.currency}</strong>)</td>
                                    <td align="right">{row.discount} (<strong>{row.agent_commission_type}</strong>)</td>
                                    <td align="right">{row.grand_total} (<strong>{row.currency}</strong>)</td>
                                </tr>
                            )}
                        </tbody>
                    </table>
                </div>
            </div>
        </>
    );
}

export default MonthlySalesList;
