import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import { LazyLoadImage } from 'react-lazy-load-image-component';
import 'react-lazy-load-image-component/src/effects/blur.css';
import { API_ENDPOINT } from "../common/Constants";
import Loader from "../components/Loader";
import Knowlegdepage from "../components/cuverse/Knowlegdepage"


function CuVerseProjectPage() {
    let publicPath = process.env.PUBLIC_URL;

    const [projectsLoading, setProjectsLoading] = useState(false);
    const [projects, setProjects] = useState([]);

    async function fetchProjects() {
        setProjectsLoading(true);
        try {
            const res = await axios.get(API_ENDPOINT + "projects/list");
            if (res.data.data.projects) {
                setProjectsLoading(false);
                setProjects(res.data.data.projects);
            }
        } catch (error) {
            console.error("error ", error);
            setProjectsLoading(false);
        }
    }

    useEffect(() => {
        window.scrollTo(0, 0);
        fetchProjects();
    }, []);

    return (
        <>
            <div className="mt-70">
                <div className="cuverseProjectsSection">
                    <div className="cuverseHeading">
                        <img src={publicPath + "/assets/images/bannerimages/cuversebanner.jpg"} className="img-fluid w-100" />
                    </div>
                    <div className="container">
                        <div className="projectsCuverse">
                            <div className="row">
                                <div className="col-md-6">
                                    <img src={publicPath + "/assets/images/missionvision.png"} className="img-fluid" />
                                </div>
                                <div className="col-md-6">
                                    <h2 className="projectsHeading vegonHeading">Projects</h2>
                                    {projectsLoading ? (
                                        <> <Loader /> </>
                                    ) : (
                                        <>
                                            {projects.length > 0 ?
                                                <>
                                                    <ul>
                                                        {projects.map((row, i) =>
                                                            <div className="" key={row.id} >
                                                                <Link to={"/culibrary/" + row.slug}>
                                                                    <li className="skylandContentSection mb-1">
                                                                        <h4>{row.title}</h4>
                                                                    </li>
                                                                </Link>
                                                            </div>
                                                        )}
                                                    </ul>

                                                </>
                                                :
                                                <>
                                                    <div className="text-center text-light">No data available.</div>
                                                </>
                                            }
                                        </>
                                    )}
                                </div>

                            </div>
                        </div>
                    </div>
                    <div>
                        < Knowlegdepage />
                    </div>
                </div>
            </div>
        </>
    )
}
export default CuVerseProjectPage;
