import { useEffect, useState } from 'react';
import { useNavigate, useLocation, Navigate, Link } from 'react-router-dom';
import axios from 'axios';
import Spinner from 'react-bootstrap/Spinner';
import Modal from 'react-bootstrap/Modal';
import { API_ENDPOINT } from "../../common/Constants";

function CreateAppointment(props) {
    let publicPath = process.env.PUBLIC_URL;

    const [countriesLoading, setCountriesLoading] = useState(false);
    const [countries, setCountries] = useState([]);

    const [loading, setLoading] = useState(false);
    const [formData, setFormData] = useState({ first_name: null, last_name: null, location: null, appointment_date: null, note: null, customer_name: null });
    const [securityCode, setSecurityCode] = useState(null);

    useEffect(() => {

        genRandomString();
        setLoading(false);
        fetchCountries();

    }, []);

    async function fetchCountries() {
        setCountriesLoading(true);
        try {
            const res = await axios.get(publicPath + "/assets/data/countries.json");
            if (res.data) {
                //console.log("res.data ",res.data.data);
                setCountriesLoading(false);
                setCountries(res.data.data);
            }
        } catch (error) {
            console.error("error ", error);
            setCountriesLoading(false);
        }
    }

    const handleChangeCountry = (e) => {
        e.persist();
        let selectedCountryName = e.target.value;
        setFormData(formData => ({ ...formData, country: selectedCountryName }));
        let countriesList = countries;
        let newDialCode = Object.keys(countriesList).filter((x) => {
            return countriesList[x].country_name === selectedCountryName;
        }).map((x) => {
            return countriesList[x].dial_code;
        });
        let dial_code = newDialCode.length ? newDialCode[0] : '';
        setFormData(formData => ({ ...formData, dial_code: dial_code }));
    }

    const handleChangeTerms = (status) => {
        console.log("terms ",status);
        setFormData(formData => ({ ...formData, terms: status }));
    }  

    const genRandomString = () => {
        var text = "";
        var length = 6;
        var possible = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
        for (var i = 0; i < length; i++) {
            text += possible.charAt(Math.floor(Math.random() * possible.length));
        }
        setSecurityCode(text);
        return text;
    }

    const resetFrom = () => {
        setFormData({ first_name: null, last_name: null, location: null, appointment_date: null, appointment_time: null, location: null, note: null, customer_name: null });

        document.getElementById("form1").reset();
    }

    const onSubmit = (e) => {
        e.preventDefault();
        console.log("formData ", formData);
        if (formData.location === null) {
            alert("Please enter location/project name.");
            return false;
        }
        if (formData.customer_name === null) {
            alert("Please enter customer name.");
            return false;
        }
        if (formData.appointment_date === null) {
            alert("Please select appointment date.");
            return false;
        }
        if (formData.appointment_time === null) {
            alert("Please select appointment time.");
            return false;
        }
        if (formData.note === null) {
            alert("Please enter appointment detail.");
            return false;
        }
        setLoading(true);

        axios.post(API_ENDPOINT + 'orgzit/create_appointment', formData)
            .then((res) => {
                console.log('res ### ', res.status, res.data)
                if (res.status === 200) {
                    setLoading(false);
                    genRandomString();
                    resetFrom();
                    setTimeout(() => {
                        alert(res.data.message);
                    }, 1000);
                }
            }).catch((error) => {
                setLoading(false);
                console.log("errors ### ", error);
                if (error) {
                    if (error.response.status === 422) {
                        let errors = error.response.data.errors;
                        let msg = error.response.data.message + "\n";
                        Object.keys(errors).map((error, index) => (
                            msg += errors[error][0] + "\n"
                        ));
                        alert(msg);
                    }
                }
            })
    }


    return (
        <>
            <Modal {...props} className="enrollmentModal" backdrop="static" keyboard={false} size="lg" aria-labelledby="contained-modal-title-vcenter" centered>
                <Modal.Header closeButton>
                    <Modal.Title id="contained-modal-title-vcenter">
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body className="p-3 enrollmentModal">
                    <div className="row align-items-center">

                        <div className="col-md-12">
                            <div className="enrollmentContent">
                                <h4>Create Appointment</h4>
                                <form id="form1" onSubmit={onSubmit}>
                                    <div className="row">
                                        <div className="col mb-3">
                                            <input className="form-control" type="text" placeholder="First Name" name="first_name" defaultValue={formData.first_name} onChange={(e) => setFormData({ ...formData, first_name: e.target.value })} />
                                        </div>
                                        <div className="col mb-3">
                                            <input className="form-control" type="text" placeholder="Last Name" name="last_name" defaultValue={formData.last_name} onChange={(e) => setFormData({ ...formData, last_name: e.target.value })} />
                                        </div>
                                    </div>
                                    <div className="row">
                                        <div className="col mb-3">
                                            <input className="form-control" type="text" placeholder="Location" name="location" defaultValue={formData.location} onChange={(e) => setFormData({ ...formData, location: e.target.value })} />
                                        </div>
                                    </div>
                                    <div className="row">
                                        <div className="col mb-3">
                                            <input className="form-control" type="text" placeholder="Customer Name" name="customer_name" defaultValue={formData.customer_name} onChange={(e) => setFormData({ ...formData, customer_name: e.target.value })} />
                                        </div>
                                    </div>
                                    <div className="row">
                                        <div className="col-md-6 mb-3">
                                            <label className='mb-1'>Appointment Date</label>
                                            <input className="form-control" type="date" placeholder="Appointment Date" name="appointment_date" defaultValue={formData.appointment_date} onChange={(e) => setFormData({ ...formData, appointment_date: e.target.value })} />
                                        </div>
                                        <div className="col-md-6 mb-3">
                                            <label className='mb-1'>Appointment Time</label>
                                            <input className="form-control" type="time" placeholder="Appointment Time" name="appointment_time" defaultValue={formData.appointment_time} onChange={(e) => setFormData({ ...formData, appointment_time: e.target.value })} />
                                        </div>
                                    </div>
                                    <div className="row">
                                        <div className="col mb-3">
                                            <textarea className="form-control" name="note" placeholder="Appointment Details" defaultValue={formData.note} onChange={(e) => setFormData({ ...formData, note: e.target.value })} >
                                            </textarea>
                                        </div>
                                    </div>


                                    <div className="row">
                                        <div className="col">                                            
                                            {loading ?
                                                <button className="btntheme" type="button" disabled >
                                                    <Spinner as="span" animation="border" size="sm" role="status" aria-hidden="true" />  Submitting...
                                                </button>
                                                :
                                                <button className="btntheme" type="submit">Submit</button>
                                            }

                                        </div>

                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>

                </Modal.Body>
            </Modal>
        </>
    );
}

export default CreateAppointment;
